#include <fstream>

#include "big_int.h"
#include "big_util.h"

namespace alice {
	// These two numbers must be primes
	const mpz_class p("", 10);
	const mpz_class q("", 10);
}



int main() {
	std::ofstream pub("alice.public.h", std::ios::out);
	std::ofstream pri("alice.private.h", std::ios::out);
	
	integer_t n = alice::p * alice::q;
	integer_t phi_n = (alice::p - 1) * (alice::q - 1);
	integer_t b;
	integer_t b_inv;
	
	set_b_and_b_inv(b, b_inv, phi_n);
	
	pri << "#pragma once" << std::endl;
	pri << "#include \"big_int.h\"" << std::endl;
	pri << "namespace alice {" << std::endl;
	pri << "\tconst mpz_class p(\"" << alice::p << "\");" << std::endl;
	pri << "\tconst mpz_class q(\"" << alice::q << "\");" << std::endl;
	pri << "\tconst mpz_class b_inv(\"" << b_inv << "\");" << std::endl;
	pri << "}" << std::endl;
	
	pub << "#pragma once" << std::endl;
	pub << "#include \"big_int.h\"" << std::endl;
	pub << "#include <gmpxx.h>" << std::endl;
	pub << "namespace alice {" << std::endl;
	pub << "\tconst mpz_class n(\"" << n << "\");" << std::endl;
	pub << "\tconst mpz_class b(\"" << b << "\");" << std::endl;
	pub << "}" << std::endl;
	
}
